// Taken from https://github.com/terraforming-mars/terraforming-mars

type GameModule =
  | "base"
  | "corpera"
  | "promo"
  | "venus"
  | "colonies"
  | "prelude"
  | "prelude2"
  | "turmoil"
  | "community"
  | "ares"
  | "moon"
  | "pathfinders"
  | "ceo"
  | "starwars"
  | "underworld";

type Tag =
  | "building"
  | "space"
  | "science"
  | "power"
  | "earth"
  | "jovian"
  | "venus"
  | "plant"
  | "microbe"
  | "animal"
  | "city"
  | "moon"
  | "mars"
  | "wild"
  | "event"
  | "clone";

type CardType =
  | "event"
  | "active"
  | "automated"
  | "prelude"
  | "corporation"
  | "ceo"
  | "standard_project"
  | "standard_action"
  | "proxy";

export type ProductionResource =
  | "plants"
  | "energy"
  | "megacredits"
  | "titanium"
  | "steel"
  | "heat";

type AddResourceType = {
  type?: "Floater" | "Data" | "Animal" | "Microbe" | "Fighter" | "Asteroid";
  tag?: Tag | Tag[];
  per?: number;
  oceans?: {};
  greeneries?: {};
  floaters?: {};
  colonies?: {};
  underworld?: unknown;
  count?: number | { tag: Tag };
  autoSelect?: boolean;
  min?: number;
  mustHaveCard?: boolean;
  robotCards?: boolean;
  moon?: {
    miningRate?: {};
    habitat?: {};
    habitatRate?: {};
    road?: {};
    mine?: {};
  };
  cities?: {
    where?: "offmars" | "onmars";
  };
  start?: number;
  resourcesHere?: {};
  each?: number;
  all?: boolean;
  others?: boolean;
};

type Behavior = {
  production?: {
    [key in ProductionResource]?: number | AddResourceType;
  };
  stock?: {
    [key in ProductionResource]?: number | AddResourceType;
  };
  removeAnyPlants?: number;
  drawCard?:
    | number
    | {
        tag?: Tag;
        type?: "event";
        count?:
          | number
          | {
              tag?: Tag;
              per?: number;
            };
        keep?: number;
      };
  decreaseAnyProduction?: {
    type?: ProductionResource;
    count?: number;
  };
  tr?:
    | number
    | {
        underworld: unknown;
        per?: number;
      };
  standardResource?: number;
  city?: {
    space?: string;
    on?: "isolated";
  };
  ocean?: {
    count?: number;
    on?: "land";
  };
  greenery?: {
    on?: "ocean";
  };
  global?: {
    venus?: number;
    temperature?: number;
    oxygen?: number;
  };
  moon?: unknown;
  turmoil?: unknown;
  underworld?: unknown;
  colonies?: unknown;
  addResourcesToAnyCard?: AddResourceType | AddResourceType[];
  addResources?:
    | number
    | {
        tag: Tag;
      };
  titanumValue?: number;
  steelValue?: number;
  greeneryDiscount?: number;
  tile?: unknown;
  spend?: {
    energy?: number;
    cards?: number;
    resourceFromAnyCard?: { type: "Microbe" };
  };
  title?: string;
};

type Action = {
  addResourcesToAnyCard?: AddResourceType;
  standardResource?: number;
  addResources?:
    | number
    | {
        tag?: Tag;
        per?: number;
      };
  tr?: number;
  stock?: {
    [key in ProductionResource]?: number | AddResourceType;
  };
  production?: {
    [key in ProductionResource]?: number;
  };
  global?: {
    venus?: number;
    temperature?: number;
    oxygen?: number;
  };
  spend?: {
    corruption?: number;
    resourcesHere?: number;
  } & {
    [key in ProductionResource]?: number;
  };
  drawCard?:
    | number
    | {
        count?: number;
        tag?: string;
        pay?: boolean;
      };
  colonies?: {
    addTradeFleet?: number;
  };
  underworld?: unknown;
  turmoil?: unknown;
  title?: string;
  log?: string;
};

export type Card = {
  id: string;
  module: GameModule;
  name: string;
  tags: Tag[];
  victoryPoints?: number | "special" | AddResourceType;
  cost: number;
  type: CardType;
  html: string;
  requirements: {
    tag?: Tag;
    oxygen?: number;
    temperature?: number;
    venus?: number;
    cities?: number;
    greeneries?: number;
    oceans?: number;
    tr?: number;
    count?: number;
    colonies?: number;
    party?: string;
    excavation?: number;
    partyLeader?: number;
    habitatTiles?: number;
    miningTiles?: number;
    roadTiles?: number;
    miningRate?: number;
    habitatRate?: number;
    logisticRate?: number;
    corruption?: number;
    all?: boolean;
    nextTo?: boolean;
    max?: boolean;
    production?: ProductionResource;
    chairman?: boolean | number;
    text?: "ON MARS";
    floaters?: number;
    plantsRemoved?: boolean;
    resourceTypes?: number;
  }[];
  effect?: {
    steelValue?: number;
    titanumValue?: number;
  };
  behavior?: Behavior & {
    or?: {
      autoSelect?: boolean;
      behaviors: Behavior[];
    };
  };
  action?: Action & {
    or?: {
      autoSelect?: boolean;
      behaviors: Action[];
    };
  };
  startingMegaCredits?: unknown;
  compatibility: unknown;
  resourceType?:
    | "Floater"
    | "Animal"
    | "Director"
    | "Asteroid"
    | "Science"
    | "Syndicate Fleet"
    | "Activist"
    | "Tool"
    | "Specialized Robot"
    | "Camp"
    | "Ware"
    | "Journalism"
    | "Orbital"
    | "Hydroelectric resource"
    | "Venusian Habitat"
    | "Clone Trooper"
    | "Graphene"
    | "Seed"
    | "Data"
    | "Microbe"
    | "Agenda"
    | "Fighter"
    | "Supply Chain"
    | "Disease"
    | "Preservation"
    | "Resource cube";
  cardDiscount?: unknown;
  cardCost?: unknown;
  metadata: {
    cardNumber?: string;
    description?: string | unknown;
    victoryPoints?: unknown;
    hasExternalHelp?: unknown;
  };
};

