export function getEligibleCards() {
  return allCards
    .filter((card) => {
      // Only consider project cards, preludes and promos
      return [
        "automated",
        "active",
        "event",
        "prelude",
        "corporation",
      ].includes(card.type);
    })
    .filter((card) => {
      // Only consider cards that are part of the core game
      return [
        "promo",
        "base",
        "prelude",
        "corpera",
        "turmoil",
        "venus",
        "colonies",
      ].includes(card.module);
    });
}

let cardByIdCached: Record<string, Card> | null = null;

export function getCardById(id: string): Card {
  if (!cardByIdCached) {
    cardByIdCached = Object.fromEntries(
      getEligibleCards().map((card) => [card.id, card]),
    );
  }
  const card = cardByIdCached[id];
  if (!card) {
    throw new Error(`Card with id ${id} not found`);
  }
  return card;
}

export function isValidCardId(id: string): boolean {
  try {
    getCardById(id);
  } catch (e) {
    console.error(e);
    return false;
  }
  return true;
}
